pragma solidity ^0.8.12;

contract Guarded {
    mapping(address => bool) private guardians;

    event GuardianAdded(address newGuardian);
    event GuardianRemoved(address removedGuardian);

    function isGuardian(address _address) public view returns (bool) {
        return guardians[_address];
    }

    function _addGuardian(address _newGuardian) internal {
        require(
            _newGuardian != address(0),
            "Guarded:: zero address can't be guardian"
        );
        require(!guardians[_newGuardian], "Guarded:: already a guardian");
        emit GuardianAdded(_newGuardian);
        guardians[_newGuardian] = true;
    }

    function _removeGuardian(address _guardian) internal {
        require(guardians[_guardian], "Guarded:: guardian doesn't exist");
        emit GuardianRemoved(_guardian);
        guardians[_guardian] = false;
    }
}
","keccak256":"0x2587722dc82ea1abcb7df626373ea701f25503ba5604b50432d394a2a72eb29d","license":"MIT"},"src/access/Owned.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

contract Owned {
    mapping(address => bool) private owners;

    event OwnerAdded(address newOwner);
    event OwnerRemoved(address removedOwner);

    function isOwner(address _address) public view returns (bool) {
        return owners[_address];
    }

    function _addOwner(address _newOwner) internal {
        // no check for address(0) as used when creating wallet via BLS.
        require(!owners[_newOwner], "Owned:: already an owner");
        emit OwnerAdded(_newOwner);
        owners[_newOwner] = true;
    }

    function _removeOwner(address _owner) internal {
        require(msg.sender != _owner, "Owned:: cannot remove self");
        require(owners[_owner], "Owned:: owner doesn't exist");
        emit OwnerRemoved(_owner);
        owners[_owner] = false;
    }
}
","keccak256":"0x9b22d78851d994f3b56027a0adf61f61e7035a949de41f10387bc81ac4cc7848","license":"MIT"},"src/helpers/UniversalSignatureValidator.sol":{"content":"// As per ERC-6492 example
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

interface IERC1271Wallet {
    function isValidSignature(
        bytes32 hash,
        bytes calldata signature
    ) external view returns (bytes4 magicValue);
}

error ERC1271Revert(bytes error);
error ERC6492DeployFailed(bytes error);

contract UniversalSigValidator {
    bytes32 private constant ERC6492_DETECTION_SUFFIX =
        0x6492649264926492649264926492649264926492649264926492649264926492;
    bytes4 private constant ERC1271_SUCCESS = 0x1626ba7e;

    function isValidSigImpl(
        address _signer,
        bytes32 _hash,
        bytes calldata _signature,
        bool allowSideEffects
    ) public returns (bool) {
        uint contractCodeLen = address(_signer).code.length;
        bytes memory sigToValidate;
        // The order here is striclty defined in https://eips.ethereum.org/EIPS/eip-6492
        // - ERC-6492 suffix check and verification first, while being permissive in case the contract is already deployed; if the contract is deployed we will check the sig against the deployed version, this allows 6492 signatures to still be validated while taking into account potential key rotation
        // - ERC-1271 verification if there's contract code
        // - finally, ecrecover
        bool isCounterfactual = bytes32(
            _signature[_signature.length - 32:_signature.length]
        ) == ERC6492_DETECTION_SUFFIX;
        if (isCounterfactual) {
            address create2Factory;
            bytes memory factoryCalldata;
            (create2Factory, factoryCalldata, sigToValidate) = abi.decode(
                _signature[0:_signature.length - 32],
                (address, bytes, bytes)
            );

            if (contractCodeLen == 0) {
                (bool success, bytes memory err) = create2Factory.call(
                    factoryCalldata
                );
                if (!success) revert ERC6492DeployFailed(err);
            }
        } else {
            sigToValidate = _signature;
        }

        // Try ERC-1271 verification
        if (isCounterfactual || contractCodeLen > 0) {
            try
                IERC1271Wallet(_signer).isValidSignature(_hash, sigToValidate)
            returns (bytes4 magicValue) {
                bool isValid = magicValue == ERC1271_SUCCESS;

                if (
                    contractCodeLen == 0 &&
                    isCounterfactual &&
                    !allowSideEffects
                ) {
                    // if the call had side effects we need to return the
                    // result using a `revert` (to undo the state changes)
                    assembly {
                        mstore(0, isValid)
                        revert(31, 1)
                    }
                }

                return isValid;
            } catch (bytes memory err) {
                revert ERC1271Revert(err);
            }
        }

        // ecrecover verification
        require(
            _signature.length == 65,
            "SignatureValidator#recoverSigner: invalid signature length"
        );
        bytes32 r = bytes32(_signature[0:32]);
        bytes32 s = bytes32(_signature[32:64]);
        uint8 v = uint8(_signature[64]);
        if (v != 27 && v != 28) {
            revert("SignatureValidator: invalid signature v value");
        }
        return ecrecover(_hash, v, r, s) == _signer;
    }

    function isValidSigWithSideEffects(
        address _signer,
        bytes32 _hash,
        bytes calldata _signature
    ) external returns (bool) {
        return this.isValidSigImpl(_signer, _hash, _signature, true);
    }

    function isValidSig(
        address _signer,
        bytes32 _hash,
        bytes calldata _signature
    ) external returns (bool) {
        try this.isValidSigImpl(_signer, _hash, _signature, false) returns (
            bool isValid
        ) {
            return isValid;
        } catch (bytes memory error) {
            // in order to avoid side effects from the contract getting deployed, the entire call will revert with a single byte result
            uint len = error.length;
            if (len == 1) return error[0] == 0x01;
            // all other errors are simply forwarded, but in custom formats so that nothing else can revert with a single byte in the call
            else
                assembly {
                    revert(error, len)
                }
        }
    }
}

// this is a helper so we can perform validation in a single eth_call without pre-deploying a singleton
contract ValidateSigOffchain {
    constructor(address _signer, bytes32 _hash, bytes memory _signature) {
        UniversalSigValidator validator = new UniversalSigValidator();
        bool isValidSig = validator.isValidSigWithSideEffects(
            _signer,
            _hash,
            _signature
        );
        assembly {
            mstore(0, isValidSig)
            return(31, 1)
        }
    }
}
